import React from 'react';
import GridListTile from '@material-ui/core/GridListTile';

const ImageCard = props => {
  const {
    downsized: { url, height, width }
  } = props.image;

  const imageRows = () => {
    return height > 250 ? 2 : 1;
  };

  const imageCols = () => {
    return width > 250 ? 2 : 1;
  };

  return (
    <GridListTile rows={imageRows} cols={imageCols}>
      <img src={url} alt={props.alt} />
    </GridListTile>
  );
};

export default ImageCard;
