import React from 'react';

class SearchBar extends React.Component {
  state = { searchTerm: '' };

  onFormSubmit = e => {
    e.preventDefault();
    this.props.onSubmit(this.state.searchTerm);
  };

  render() {
    return (
      <form onSubmit={this.onFormSubmit}>
        <div className="ui fluid transparent input icon">
          <label for="search-bar-input">GIF Search</label>
          <i className="circular blue search link icon" />
          <input
            id="search-bar-input"
            type="text"
            placeholder="Search..."
            value={this.state.searchTerm}
            onChange={e => this.setState({ searchTerm: e.target.value })}
          />
        </div>
      </form>
    );
  }
}

export default SearchBar;
